mapper.update:
update forum
forum_title = #{forumTitle}, forum_text = #{forumText}, forum_notice = #{forumNotice}, forum_ofiles = #{forumOfiles}, forum_files = #{forumFiles},
where forum_no = #{forumNo}

mapper.select:
select * from forum where dept_no = #{deptno} 

	and ${type} like #{keyword}

order by forum_date desc limit #{min}, #{max}

